import React, { useState } from "react";
import { useKeenSlider } from "keen-slider/react";
import "keen-slider/keen-slider.min.css";

const baseScale = ["C", "C#", "D", "D#", "E", "F", "F#", "G", "G#", "A", "A#", "B"];
const majorIntervals = [0, 2, 4, 5, 7, 9, 11];
const squareSize = 70;
const rowGap = 50; // Space between rows
const topGridColumns = 12;
const bottomGridColumns = 7;

const overlayColumnCount = topGridColumns;
const borderWidth = 2;
const borderStyle = `${borderWidth}px solid #333`;
const pinkColor = "#f2c2c2";
const greyColor = "#cccccc";

function generateOctaves(octaveCount) {
  return Array.from({ length: octaveCount }, (_, i) => i + 1)
    .flatMap((octave) =>
      baseScale.map((note) => (note === "C" ? note + octave : note))
    );
}

const notes = generateOctaves(4);

export default function NotesGrid() {
  const [bottomNotes, setBottomNotes] = useState(() => {
    const rootIndex = 0;
    return majorIntervals.map((interval) =>
      notes[(rootIndex + interval) % notes.length]
    );
  });

  const [sliderRef] = useKeenSlider({
    loop: false,
    centered: false,
    slides: {
      perView: topGridColumns,
      spacing: 0,
    },
    slideChanged(s) {
      const rootIndex = s.track.details.abs;
      if (rootIndex !== undefined) {
        const updatedNotes = majorIntervals.map((interval) =>
          notes[(rootIndex + interval) % notes.length]
        );
        setBottomNotes(updatedNotes);
      }
    },
  });

  function renderNote(note) {
    if (note.startsWith("C") && note.length > 1 && !note.includes("#")) {
      const octave = note.slice(1);
      return (
        <>
          C<sub>{octave}</sub>
        </>
      );
    }
    return note;
  }

  return (
    <div style={{ width: "fit-content", margin: "50px auto", fontFamily: "sans-serif" }}>
      {/* TOP GRID */}
      <div
        style={{
          width: `${squareSize * topGridColumns}px`,
          height: `${squareSize}px`,
          position: "relative",
          boxSizing: "border-box",
          overflow: "hidden",
          background: "#fff",
          border: borderStyle,
          marginBottom: `${rowGap}px`,
        }}
      >
        {/* Colored boxes behind top notes */}
        <div
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            pointerEvents: "none",
            zIndex: 1,
          }}
        >
          {[...Array(overlayColumnCount)].map((_, i) => {
            const background =
              pinkIndicesTop.includes(i) ? pinkColor : greyIndicesTop.includes(i) ? greyColor : "transparent";
            const boxLeft = i * squareSize;
            return (
              <div
                key={i}
                style={{
                  position: "absolute",
                  top: 0,
                  left: `${boxLeft}px`,
                  width: `${squareSize}px`,
                  height: "100%",
                  background: background,
                }}
              />
            );
          })}
        </div>

        {/* Top notes slider */}
        <div
          ref={sliderRef}
          className="keen-slider"
          style={{
            cursor: "grab",
            position: "relative",
            zIndex: 2,
            width: "100%",
            height: "100%",
            display: "flex",
            alignItems: "center",
          }}
        >
          {notes.map((note, idx) => (
            <div
              className="keen-slider__slide"
              key={idx}
              style={{
                display: "flex",
                justifyContent: "center",
                alignItems: "center",
                width: `${squareSize}px`,
                height: `${squareSize}px`,
                fontSize: "16px",
                fontWeight: "bold",
              }}
            >
              {renderNote(note)}
            </div>
          ))}
        </div>

        {/* Vertical lines on top */}
        <div
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            pointerEvents: "none",
            zIndex: 3,
          }}
        >
          {[...Array(overlayColumnCount + 1)].map((_, i) => {
            const lineLeft = i * squareSize;
            return (
              <div
                key={i}
                style={{
                  position: "absolute",
                  top: 0,
                  left: `${lineLeft}px`,
                  width: 0,
                  height: "100%",
                  borderLeft: borderStyle,
                }}
              />
            );
          })}
        </div>
      </div>

      {/* BOTTOM GRID */}
      <div
        style={{
          width: `${squareSize * bottomGridColumns}px`,
          height: `${squareSize}px`,
          margin: "0 auto",
          position: "relative",
          boxSizing: "border-box",
          overflow: "hidden",
          background: "#fff",
          border: borderStyle,
        }}
      >
        <div
          style={{
            position: "relative",
            zIndex: 2,
            width: "100%",
            height: "100%",
            display: "flex",
            alignItems: "center",
          }}
        >
          {bottomNotes.map((note, idx) => (
            <div
              key={idx}
              style={{
                display: "flex",
                justifyContent: "center",
                alignItems: "center",
                width: `${squareSize}px`,
                height: `${squareSize}px`,
                fontSize: "16px",
                fontWeight: "bold",
                borderLeft: idx > 0 ? borderStyle : "none",
              }}
            >
              {renderNote(note)}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
