import React, { useState } from "react";
import { useKeenSlider } from "keen-slider/react";
import "keen-slider/keen-slider.min.css";

const baseScale = ["C", "C#", "D", "D#", "E", "F", "F#", "G", "G#", "A", "A#", "B"];
const majorIntervals = [0, 2, 4, 5, 7, 9, 11];
const squareSize = 70;
const rowGap = 70;

function generateOctaves(octaveCount) {
  return baseScale.flatMap((note) => {
    const octaves = [];
    for (let i = 1; i <= octaveCount; i++) {
      octaves.push(note === "C" ? note + i : note);
    }
    return octaves;
  });
}

const notes = generateOctaves(4);

export default function NotesGrid() {
  const [bottomNotes, setBottomNotes] = useState(() =>
    majorIntervals.map((interval) => notes[interval])
  );

  const [sliderRef] = useKeenSlider({
    loop: false,
    slides: {
      perView: 12,
      spacing: 0,
    },
    slideChanged(s) {
      const rootIndex = s.track.details.abs;
      if (rootIndex !== undefined) {
        const updatedNotes = majorIntervals.map(
          (interval) => notes[(rootIndex + interval) % notes.length]
        );
        setBottomNotes(updatedNotes);
      }
    },
  });

  function renderNote(note) {
    return note.startsWith("C") && note.length > 1 && !note.includes("#") ? (
      <>
        C<sub>{note.slice(1)}</sub>
      </>
    ) : (
      note
    );
  }

  return (
    <div style={{ fontFamily: "sans-serif", margin: "50px auto", textAlign: "center" }}>
      {/* TOP GRID */}
      <div
        style={{
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          width: `${squareSize * 12}px`,
          height: `${squareSize}px`,
          margin: "0 auto",
          border: "2px solid #333",
          overflow: "hidden",
          position: "relative",
        }}
      >
        <div
          ref={sliderRef}
          className="keen-slider"
          style={{
            display: "flex",
            width: "100%",
            height: "100%",
          }}
        >
          {notes.map((note, idx) => (
            <div
              key={idx}
              className="keen-slider__slide"
              style={{
                width: `${squareSize}px`,
                height: `${squareSize}px`,
                display: "flex",
                justifyContent: "center",
                alignItems: "center",
                fontSize: "16px",
                fontWeight: "bold",
              }}
            >
              {renderNote(note)}
            </div>
          ))}
        </div>
      </div>

      {/* GAP BETWEEN ROWS */}
      <div style={{ height: `${rowGap}px` }}></div>

      {/* BOTTOM GRID */}
      <div
        style={{
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          width: `${squareSize * 7}px`,
          height: `${squareSize}px`,
          margin: "0 auto",
          border: "2px solid #333",
        }}
      >
        {bottomNotes.map((note, idx) => (
          <div
            key={idx}
            style={{
              width: `${squareSize}px`,
              height: `${squareSize}px`,
              display: "flex",
              justifyContent: "center",
              alignItems: "center",
              fontSize: "16px",
              fontWeight: "bold",
              borderLeft: idx > 0 ? "2px solid #333" : "none",
            }}
          >
            {renderNote(note)}
          </div>
        ))}
      </div>
    </div>
  );
}
