import React, { useState } from "react";
import { useKeenSlider } from "keen-slider/react";
import "keen-slider/keen-slider.min.css";

const baseScale = ["C", "C#", "D", "D#", "E", "F", "F#", "G", "G#", "A", "A#", "B"];
const majorIntervals = [0, 2, 4, 5, 7, 9, 11]; // Intervals for a major scale
const squareSize = 70; // Set a common size for all squares

function generateOctaves(octaveCount) {
  const result = [];
  for (let octave = 1; octave <= octaveCount; octave++) {
    for (let note of baseScale) {
      if (note === "C") {
        result.push(note + octave);
      } else {
        result.push(note);
      }
    }
  }
  return result;
}

// Top row: full chromatic scale with sharps/flats (4 octaves)
const notes = generateOctaves(4);

// Top grid config
const perViewTop = 12;
const slideWidth = 60;
const slideSpacing = 10;
const columnWidth = slideWidth + slideSpacing; // 70px
const totalWidthTop = (perViewTop * slideWidth) + ((perViewTop - 1) * slideSpacing); // 830px
const overlayColumnsTop = 12;
const overlayStartOffsetTop = (totalWidthTop - overlayColumnsTop * columnWidth) / 2; // -5px

// Bottom grid config
const perViewBottom = 7;
const slideWidthBottom = 60;
const slideSpacingBottom = 0; // No spacing between bottom notes
const totalWidthBottom = perViewBottom * slideWidthBottom; // 7 * 60 = 420px
const containerWidthBottom = totalWidthBottom + 4; // Add border
const containerHeightBottom = slideWidthBottom + 4;

const lineBorder = "2px solid #333";
const pinkColor = "#f2c2c2";
const greyColor = "#cccccc";

const pinkIndicesTop = [0];
const greyIndicesTop = [2, 4, 5, 7, 9, 11];

export default function NotesGrid() {
  const [bottomNotes, setBottomNotes] = useState(() => {
    const rootIndex = 0; // Default root index
    return majorIntervals.map((interval) =>
      notes[(rootIndex + interval) % notes.length]
    );
  });


  const [sliderRef] = useKeenSlider({
    loop: false,
    centered: false,
    slides: {
      perView: perViewTop,
      spacing: slideSpacing,
    },
    slideChanged(s) {
      const rootIndex = s.track.details.abs; // Index of pink cell
      if (rootIndex !== undefined) {
        // Compute the notes for the bottom row
        const updatedNotes = majorIntervals.map((interval) =>
          notes[(rootIndex + interval) % notes.length]
        );

        // Set the bottom notes to these computed notes
        setBottomNotes(updatedNotes);
      }
    },
  });

  function renderNote(note) {
    if (note.startsWith("C") && note.length > 1 && !note.includes("#")) {
      const octave = note.slice(1);
      return (
        <>
          C<sub>{octave}</sub>
        </>
      );
    }
    return note;
  }

  return (
    <div style={{ width: "fit-content", margin: "50px auto", fontFamily: "sans-serif" }}>
      {/* TOP GRID */}
      <div
        style={{
          width: `${totalWidthTop}px`,
          height: "74px",
          position: "relative",
          boxSizing: "border-box",
          overflow: "hidden",
          background: "#fff",
          border: lineBorder,
          marginBottom: "20px",
        }}
      >
        {/* Colored boxes behind top notes */}
        <div
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            pointerEvents: "none",
            zIndex: 1,
          }}
        >
          {[...Array(overlayColumnsTop)].map((_, i) => {
            let background = "transparent";
            if (pinkIndicesTop.includes(i)) {
              background = pinkColor;
            } else if (greyIndicesTop.includes(i)) {
              background = greyColor;
            }
            const boxLeft = overlayStartOffsetTop + i * columnWidth;
            return (
              <div
                key={i}
                style={{
                  position: "absolute",
                  top: 0,
                  left: `${boxLeft}px`,
                  width: `${columnWidth}px`,
                  height: "100%",
                  background: background,
                }}
              />
            );
          })}
        </div>

        {/* Top notes slider */}
        <div
          ref={sliderRef}
          className="keen-slider"
          style={{
            cursor: "grab",
            position: "relative",
            zIndex: 2,
            width: "100%",
            height: "100%",
            display: "flex",
            alignItems: "center",
          }}
        >
          {notes.map((note, idx) => (
            <div
              className="keen-slider__slide"
              key={idx}
              style={{
                display: "flex",
                justifyContent: "center",
                alignItems: "center",
                width: `${squareSize}px`, // Use squareSize for width
                height: `${squareSize}px`, // Use squareSize for height
                fontSize: "16px",
                fontWeight: "bold",
              }}
            >
              {renderNote(note)}
            </div>
          ))}
        </div>

        {/* Vertical lines on top */}
        <div
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            pointerEvents: "none",
            zIndex: 3,
          }}
        >
          {[...Array(overlayColumnsTop + 1)].map((_, i) => {
            const lineLeft = overlayStartOffsetTop + i * columnWidth;
            return (
              <div
                key={i}
                style={{
                  position: "absolute",
                  top: 0,
                  left: `${lineLeft}px`,
                  width: 0,
                  height: "100%",
                  borderLeft: "2px solid #333",
                }}
              />
            );
          })}
        </div>
      </div>

      {/* BOTTOM GRID */}
      <div
        style={{
          width: `${containerWidthBottom}px`,
          height: `${containerHeightBottom}px`,
          margin: "50px auto",
          position: "relative",
          boxSizing: "border-box",
          overflow: "hidden",
          background: "#fff",
          border: lineBorder,
        }}
      >
        <div
          style={{
            position: "relative",
            zIndex: 2,
            width: "100%",
            height: "100%",
            display: "flex",
            alignItems: "center",
          }}
        >
          {bottomNotes.map((note, idx) => (
            <div
              key={idx}
              style={{
                display: "flex",
                justifyContent: "center",
                alignItems: "center",
                width: `${squareSize}px`, // Use squareSize for width
                height: `${squareSize}px`, // Use squareSize for height
                fontSize: "16px",
                fontWeight: "bold",
                borderLeft: idx > 0 ? "2px solid #333" : "none",
              }}
            >
              {renderNote(note)}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
